package wechatpay

import (
	"context"
	"io/ioutil"
	"net/http"
	"strconv"
	"strings"
	"testing"
)

func TestParseHttpRequestForPayNotification(t *testing.T) {
	client, err := mockNewClient()
	if err != nil {
		t.Fatal(err)
	}

	if client == nil {
		t.Fatal("client is nil")
	}

	cases := []struct {
		newReq func() *http.Request
		pass   bool
	}{
		{
			func() *http.Request {
				req := &http.Request{
					Header: http.Header{},
				}

				signature := ""
				mockBody := `{"id":"b62e271c-3389-58a0-8146-4a704966e8f1","create_time":"2021-01-28T17:07:11+08:00","resource_type":"encrypt-resource","event_type":"TRANSACTION.SUCCESS","summary":"支付成功","resource":{"original_type":"transaction","algorithm":"AEAD_AES_256_GCM","ciphertext":"","associated_data":"transaction","nonce":"fG1l57vn9BCX"}}`

				req.Header.Set("Wechatpay-Nonce", mockNonce)
				req.Header.Set("Wechatpay-Signature", signature)
				req.Header.Set("Wechatpay-Timestamp", strconv.FormatInt(mockTimestamp, 10))
				req.Header.Set("Wechatpay-Serial", mockSerialNo)
				req.Body = ioutil.NopCloser(strings.NewReader(mockBody))

				return req
			},
			true,
		},
		{
			func() *http.Request {
				req := &http.Request{
					Header: http.Header{},
				}

				mockBody := `{"id":"b62e271c-3389-58a0-8146-4a704966e8f1","create_time":"2021-01-28T17:07:11+08:00","resource_type":"encrypt-resource","event_type":"TRANSACTION.SUCCESS","summary":"支付成功","resource":{"original_type":"transaction","algorithm":"AEAD_AES_256_GCM","ciphertext":"","associated_data":"transaction","nonce":"fG1l57vn9BCX"}}`

				req.Header.Set("Wechatpay-Nonce", mockNonce)
				req.Header.Set("Wechatpay-Timestamp", "xxx")
				req.Header.Set("Wechatpay-Serial", mockSerialNo)
				req.Body = ioutil.NopCloser(strings.NewReader(mockBody))

				return req
			},
			false,
		},
	}

	for _, c := range cases {
		n := PayNotification{}
		req := c.newReq()
		_, err := n.ParseHttpRequest(client, req)
		pass := err == nil
		if pass != c.pass {
			t.Fatalf("expect %v, got %v, err %v", c.pass, pass, err)
		}
	}
}

func TestParseForPayNotification(t *testing.T) {
	client, err := mockNewClient()
	if err != nil {
		t.Fatal(err)
	}

	if client == nil {
		t.Fatal("client is nil")
	}

	cases := []struct {
		result *Result
		pass   bool
	}{
		{
			&Result{
				Timestamp: mockTimestamp,
				Nonce:     mockNonce,
				Signature: "",
				SerialNo:  mockSerialNo,
				Body:      []byte(`{"id":"b62e271c-3389-58a0-8146-4a704966e8f1","create_time":"2021-01-28T17:07:11+08:00","resource_type":"encrypt-resource","event_type":"TRANSACTION.SUCCESS","summary":"支付成功","resource":{"original_type":"transaction","algorithm":"AEAD_AES_256_GCM","ciphertext":"","associated_data":"transaction","nonce":"fG1l57vn9BCX"}}`),
			},
			true,
		},
		{
			&Result{
				Timestamp: mockTimestamp,
				Nonce:     mockNonce,
				Signature: "",
				SerialNo:  mockSerialNo,
				Body:      []byte(`{`),
			},
			false,
		},
		{
			&Result{
				Timestamp: mockTimestamp,
				Nonce:     mockNonce,
				Signature: "",
				SerialNo:  mockSerialNo,
				Body:      []byte(`{"id":"b62e271c-3389-58a0-8146-4a704966e8f1","create_time":"2021-01-28T17:07:11+08:00","resource_type":"encrypt-resource","event_type":"TRANSACTION.SUCCESS","summary":"支付成功","resource":{"original_type":"transaction","algorithm":"AEAD_AES_256_GCM","ciphertext":"yriQVTmulDmba+GD/be3QUMtoDg=","associated_data":"transaction","nonce":"fG1l57vn9BCX"}}`),
			},
			false,
		},
	}

	ctx := context.Background()
	for _, c := range cases {
		n := PayNotification{}
		_, err := n.Parse(ctx, client, c.result)
		pass := err == nil
		if pass != c.pass {
			t.Fatalf("expect %v, got %v, err %v", c.pass, pass, err)
		}
	}
}

func TestNotificationAnswer(t *testing.T) {
	answer := &NotificationAnswer{}
	buffer := answer.Bytes()
	actual := string(buffer)
	expect := `{"code":"","message":""}`
	if expect != actual {
		t.Fatalf("expect %s, got %s", expect, actual)
	}
}
