func TestLoadRSAPrivateKeyFromTxt(t *testing.T) {
	cases := []struct {
		txt    string
		pass   bool
		expect string
	}{
		{
			mockRSAPrivateKeyCert,
			true,
			"",
		},
		{
			"-----BEGIN PRIVATE KEY-----",
			false,
			"",
		},
		{
			mockInvalidRSAPrivateKeyCert,
			false,
			"",
		},
		{
			mockRSAPublicKeyCert,
			false,
			"",
		},
	}

	for _, c := range cases {
		privateKey, err := LoadRSAPrivateKeyFromTxt(c.txt)
		pass := err == nil
		if pass != c.pass {
			t.Fatalf("expect %v, got %v, %v", c.pass, pass, err)
		}

		if err != nil {
			continue
		}

		actual := privateKey.N.String()
		if actual != c.expect {
			t.Fatalf("expect %s, got %s", c.expect, actual)
		}
	}
}

