func TestLoadRSAPublicKey(t *testing.T) {
	cases := []struct {
		key    []byte
		pass   bool
		expect string
	}{
		{
			[]byte(mockRSAPublicKeyCert),
			true,
			"",
		},
		{
			[]byte("-----BEGIN CERTIFICATE-----"),
			false,
			"",
		},
		{
			[]byte(mockInvalidRSAPrivateKeyCert),
			false,
			"",
		},
	}

	for _, c := range cases {
		publicKey, err := LoadRSAPublicKeyFromCert(c.key)
		pass := err == nil
		if pass != c.pass {
			t.Fatalf("expect %v, got %v, %v", c.pass, pass, err)
		}

		if err != nil {
			continue
		}

		actual := publicKey.N.String()
		if actual != c.expect {
			t.Fatalf("expect %s, got %s", c.expect, actual)
		}
	}
}

